import React, { useState, useEffect } from 'react';

function ReviewsRatings({ itemName, itemId }) {
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({ rating: 5, comment: '' });
  const [showForm, setShowForm] = useState(false);
  const [filter, setFilter] = useState('all'); // all, 5, 4, 3, 2, 1
  const [sortBy, setSortBy] = useState('recent'); // recent, helpful, highest, lowest

  useEffect(() => {
    // Load reviews from localStorage based on itemId
    const savedReviews = localStorage.getItem(`reviews_${itemId}`);
    if (savedReviews) {
      setReviews(JSON.parse(savedReviews));
    } else {
      // Default reviews for demo
      setReviews([
        {
          id: 1,
          user: 'Ahmed Khan',
          rating: 5,
          comment: 'Absolutely delicious! Best biryani on train. The spices were perfectly balanced and the meat was tender.',
          date: new Date(Date.now() - 2 * 24 * 60 * 60 * 1000).toISOString(),
          helpful: 12,
          avatar: 'üë®'
        },
        {
          id: 2,
          user: 'Fatima Rahman',
          rating: 4,
          comment: 'Good taste, delivered on time. Portion size was generous.',
          date: new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString(),
          helpful: 8,
          avatar: 'üë©'
        },
        {
          id: 3,
          user: 'Karim Ahmed',
          rating: 5,
          comment: 'Fresh and hot! Highly recommended. Will definitely order again.',
          date: new Date(Date.now() - 14 * 24 * 60 * 60 * 1000).toISOString(),
          helpful: 15,
          avatar: 'üë®'
        }
      ]);
    }
  }, [itemId]);

  const saveReviews = (updatedReviews) => {
    setReviews(updatedReviews);
    localStorage.setItem(`reviews_${itemId}`, JSON.stringify(updatedReviews));
  };

  const averageRating = reviews.length > 0
    ? (reviews.reduce((sum, r) => sum + r.rating, 0) / reviews.length).toFixed(1)
    : 0;

  const ratingDistribution = {
    5: reviews.filter((r) => r.rating === 5).length,
    4: reviews.filter((r) => r.rating === 4).length,
    3: reviews.filter((r) => r.rating === 3).length,
    2: reviews.filter((r) => r.rating === 2).length,
    1: reviews.filter((r) => r.rating === 1).length
  };

  const handleSubmitReview = (e) => {
    e.preventDefault();
    
    if (!newReview.comment.trim()) {
      alert('Please write a review');
      return;
    }

    const review = {
      id: Date.now(),
      user: 'You',
      rating: newReview.rating,
      comment: newReview.comment,
      date: new Date().toISOString(),
      helpful: 0,
      avatar: 'üòä'
    };

    const updatedReviews = [review, ...reviews];
    saveReviews(updatedReviews);
    setNewReview({ rating: 5, comment: '' });
    setShowForm(false);
  };

  const handleHelpful = (reviewId) => {
    const updatedReviews = reviews.map((r) =>
      r.id === reviewId ? { ...r, helpful: r.helpful + 1 } : r
    );
    saveReviews(updatedReviews);
  };

  const renderStars = (rating, size = '1rem') => {
    return (
      <span style={{ fontSize: size, color: '#ffa500', letterSpacing: '2px' }}>
        {'‚òÖ'.repeat(rating)}{'‚òÜ'.repeat(5 - rating)}
      </span>
    );
  };

  const getRelativeTime = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInSeconds = Math.floor((now - date) / 1000);

    if (diffInSeconds < 60) return 'Just now';
    if (diffInSeconds < 3600) return `${Math.floor(diffInSeconds / 60)} minutes ago`;
    if (diffInSeconds < 86400) return `${Math.floor(diffInSeconds / 3600)} hours ago`;
    if (diffInSeconds < 604800) return `${Math.floor(diffInSeconds / 86400)} days ago`;
    if (diffInSeconds < 2592000) return `${Math.floor(diffInSeconds / 604800)} weeks ago`;
    return `${Math.floor(diffInSeconds / 2592000)} months ago`;
  };

  // Filter and sort reviews
  let filteredReviews = filter === 'all' 
    ? reviews 
    : reviews.filter((r) => r.rating === parseInt(filter));

  if (sortBy === 'recent') {
    filteredReviews = [...filteredReviews].sort((a, b) => new Date(b.date) - new Date(a.date));
  } else if (sortBy === 'helpful') {
    filteredReviews = [...filteredReviews].sort((a, b) => b.helpful - a.helpful);
  } else if (sortBy === 'highest') {
    filteredReviews = [...filteredReviews].sort((a, b) => b.rating - a.rating);
  } else if (sortBy === 'lowest') {
    filteredReviews = [...filteredReviews].sort((a, b) => a.rating - b.rating);
  }

  return (
    <div className="reviews-section" style={{ marginTop: '3rem' }}>
      {/* Header Section */}
      <div className="reviews-header">
        <h3>Reviews & Ratings</h3>
      </div>

      {/* Rating Summary */}
      <div style={{
        background: 'var(--dark-lighter)',
        padding: '2rem',
        borderRadius: '15px',
        marginBottom: '2rem',
        display: 'grid',
        gridTemplateColumns: '200px 1fr',
        gap: '2rem',
        alignItems: 'center'
      }}>
        {/* Average Rating */}
        <div style={{ textAlign: 'center', borderRight: '1px solid var(--border-dark)' }}>
          <div style={{ fontSize: '4rem', fontWeight: 'bold', color: 'var(--primary-orange)' }}>
            {averageRating}
          </div>
          <div style={{ fontSize: '1.5rem', marginBottom: '0.5rem' }}>
            {renderStars(Math.round(averageRating), '1.5rem')}
          </div>
          <div style={{ color: 'var(--text-gray)', fontSize: '0.9rem' }}>
            Based on {reviews.length} review{reviews.length !== 1 ? 's' : ''}
          </div>
        </div>

        {/* Rating Distribution */}
        <div>
          {[5, 4, 3, 2, 1].map((star) => {
            const count = ratingDistribution[star];
            const percentage = reviews.length > 0 ? (count / reviews.length) * 100 : 0;
            return (
              <div
                key={star}
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '1rem',
                  marginBottom: '0.5rem',
                  cursor: 'pointer'
                }}
                onClick={() => setFilter(filter === star.toString() ? 'all' : star.toString())}
              >
                <span style={{ color: 'var(--text-white)', width: '50px' }}>{star} ‚òÖ</span>
                <div style={{
                  flex: 1,
                  height: '8px',
                  background: 'var(--dark-bg)',
                  borderRadius: '10px',
                  overflow: 'hidden'
                }}>
                  <div style={{
                    width: `${percentage}%`,
                    height: '100%',
                    background: star >= 4 ? 'var(--primary-orange)' : star >= 3 ? '#ffa500' : '#888',
                    transition: 'width 0.3s'
                  }}></div>
                </div>
                <span style={{ color: 'var(--text-gray)', width: '40px', fontSize: '0.9rem' }}>
                  {count}
                </span>
              </div>
            );
          })}
        </div>
      </div>

      {/* Filter and Sort Controls */}
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: '1.5rem',
        gap: '1rem',
        flexWrap: 'wrap'
      }}>
        <div style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
          <button
            className={`btn btn-sm ${filter === 'all' ? 'btn-primary' : 'btn-secondary'}`}
            onClick={() => setFilter('all')}
            style={{ padding: '0.5rem 1rem' }}
          >
            All ({reviews.length})
          </button>
          {[5, 4, 3, 2, 1].map((star) => (
            <button
              key={star}
              className={`btn btn-sm ${filter === star.toString() ? 'btn-primary' : 'btn-secondary'}`}
              onClick={() => setFilter(star.toString())}
              style={{ padding: '0.5rem 1rem' }}
            >
              {star} ‚òÖ ({ratingDistribution[star]})
            </button>
          ))}
        </div>

        <select
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
          style={{
            padding: '0.5rem 1rem',
            background: 'var(--dark-input)',
            border: '1px solid var(--border-dark)',
            borderRadius: '5px',
            color: 'var(--text-white)',
            cursor: 'pointer'
          }}
        >
          <option value="recent">Most Recent</option>
          <option value="helpful">Most Helpful</option>
          <option value="highest">Highest Rating</option>
          <option value="lowest">Lowest Rating</option>
        </select>
      </div>

      {/* Write Review Button */}
      <button
        className="btn btn-primary btn-block"
        onClick={() => setShowForm(!showForm)}
        style={{ marginBottom: '2rem' }}
      >
        {showForm ? '‚ùå Cancel' : '‚úçÔ∏è Write a Review'}
      </button>

      {/* Review Form */}
      {showForm && (
        <form onSubmit={handleSubmitReview} className="review-form" style={{
          background: 'var(--dark-lighter)',
          padding: '2rem',
          borderRadius: '15px',
          marginBottom: '2rem',
          border: '2px solid var(--primary-orange)'
        }}>
          <h4 style={{ color: 'var(--primary-orange)', marginBottom: '1.5rem' }}>Share Your Experience</h4>
          
          <div className="form-group">
            <label>Your Rating</label>
            <div className="star-selector" style={{
              display: 'flex',
              gap: '0.5rem',
              marginTop: '0.5rem'
            }}>
              {[1, 2, 3, 4, 5].map((star) => (
                <span
                  key={star}
                  className={`star-btn ${star <= newReview.rating ? 'active' : ''}`}
                  onClick={() => setNewReview({ ...newReview, rating: star })}
                  style={{
                    fontSize: '2.5rem',
                    cursor: 'pointer',
                    color: star <= newReview.rating ? '#ffa500' : '#444',
                    transition: 'all 0.3s',
                    transform: star <= newReview.rating ? 'scale(1.1)' : 'scale(1)'
                  }}
                  onMouseEnter={(e) => e.target.style.transform = 'scale(1.2)'}
                  onMouseLeave={(e) => e.target.style.transform = star <= newReview.rating ? 'scale(1.1)' : 'scale(1)'}
                >
                  {star <= newReview.rating ? '‚òÖ' : '‚òÜ'}
                </span>
              ))}
            </div>
          </div>

          <div className="form-group" style={{ marginTop: '1.5rem' }}>
            <label>Your Review</label>
            <textarea
              value={newReview.comment}
              onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
              placeholder="Tell us about your experience with this item..."
              rows="5"
              required
              style={{
                width: '100%',
                padding: '1rem',
                background: 'var(--dark-input)',
                border: '1px solid var(--border-dark)',
                borderRadius: '10px',
                color: 'var(--text-white)',
                fontSize: '1rem',
                resize: 'vertical',
                marginTop: '0.5rem'
              }}
            />
            <small style={{ color: 'var(--text-gray)', fontSize: '0.85rem' }}>
              Minimum 10 characters ({newReview.comment.length}/10)
            </small>
          </div>

          <button 
            type="submit" 
            className="btn btn-primary btn-block"
            disabled={newReview.comment.length < 10}
            style={{ marginTop: '1rem' }}
          >
            Submit Review
          </button>
        </form>
      )}

      {/* Reviews List */}
      <div className="reviews-list">
        {filteredReviews.length === 0 ? (
          <div style={{
            textAlign: 'center',
            padding: '3rem',
            background: 'var(--dark-lighter)',
            borderRadius: '15px',
            color: 'var(--text-gray)'
          }}>
            <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>üìù</div>
            <h3>No reviews yet</h3>
            <p>Be the first to review this item!</p>
          </div>
        ) : (
          filteredReviews.map((review) => (
            <div key={review.id} className="review-card" style={{
              background: 'var(--dark-lighter)',
              padding: '1.5rem',
              borderRadius: '15px',
              marginBottom: '1rem',
              border: '1px solid var(--border-dark)',
              transition: 'all 0.3s'
                        }}>
              <div className="review-header" style={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'flex-start',
                marginBottom: '1rem',
                flexWrap: 'wrap',
                gap: '1rem'
              }}>
                <div className="reviewer-info" style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                  <span className="reviewer-avatar" style={{
                    width: '50px',
                    height: '50px',
                    borderRadius: '50%',
                    background: 'var(--primary-orange)',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '1.8rem'
                  }}>
                    {review.avatar}
                  </span>
                  <div>
                    <h4 style={{ margin: 0, color: 'var(--text-white)', fontSize: '1.1rem' }}>
                      {review.user}
                    </h4>
                    <span className="review-date" style={{ 
                      color: 'var(--text-gray)', 
                      fontSize: '0.85rem',
                      display: 'block',
                      marginTop: '0.2rem'
                    }}>
                      {getRelativeTime(review.date)}
                    </span>
                  </div>
                </div>
                <div className="review-rating">
                  {renderStars(review.rating, '1.3rem')}
                </div>
              </div>

              <p className="review-comment" style={{
                color: 'var(--text-gray)',
                lineHeight: '1.7',
                marginBottom: '1rem',
                fontSize: '0.95rem'
              }}>
                {review.comment}
              </p>

              {/* Helpful Button */}
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '1rem',
                paddingTop: '1rem',
                borderTop: '1px solid var(--border-dark)'
              }}>
                <button
                  onClick={() => handleHelpful(review.id)}
                  style={{
                    background: 'transparent',
                    border: '1px solid var(--border-dark)',
                    color: 'var(--text-gray)',
                    padding: '0.5rem 1rem',
                    borderRadius: '20px',
                    cursor: 'pointer',
                    fontSize: '0.85rem',
                    transition: 'all 0.3s',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '0.5rem'
                  }}
                  onMouseEnter={(e) => {
                    e.target.style.borderColor = 'var(--primary-orange)';
                    e.target.style.color = 'var(--primary-orange)';
                  }}
                  onMouseLeave={(e) => {
                    e.target.style.borderColor = 'var(--border-dark)';
                    e.target.style.color = 'var(--text-gray)';
                  }}
                >
                  üëç Helpful ({review.helpful})
                </button>
                
                {review.user === 'You' && (
                  <span style={{
                    background: 'rgba(255, 107, 53, 0.2)',
                    color: 'var(--primary-orange)',
                    padding: '0.3rem 0.8rem',
                    borderRadius: '15px',
                    fontSize: '0.75rem',
                    fontWeight: '600'
                  }}>
                    Your Review
                  </span>
                )}

                {review.rating >= 4 && (
                  <span style={{
                    background: 'rgba(76, 175, 80, 0.2)',
                    color: 'var(--success)',
                    padding: '0.3rem 0.8rem',
                    borderRadius: '15px',
                    fontSize: '0.75rem',
                    fontWeight: '600'
                  }}>
                    ‚úì Verified Purchase
                  </span>
                )}
              </div>
            </div>
          ))
        )}
      </div>

      {/* Show More Button */}
      {filteredReviews.length > 5 && (
        <button
          className="btn btn-secondary btn-block"
          style={{ marginTop: '2rem' }}
          onClick={() => alert('Load more reviews functionality')}
        >
          Show More Reviews
        </button>
      )}
    </div>
  );
}

export default ReviewsRatings;
