import React, { useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useCart } from '../context/CartContext';

const Navbar = () => {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const { user, isAuthenticated, logout } = useAuth();
  const { getCartCount } = useCart();
  const navigate = useNavigate();
  const location = useLocation(); // Get current location

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const handleLogoClick = () => {
    navigate('/');
  };

  // Function to check if link is active
  const isActive = (path) => {
    return location.pathname === path;
  };

  return (
    <nav className="navbar">
      <div className="container">
        <div className="nav-wrapper">
          <div className="logo" onClick={handleLogoClick}>
            <img 
              src="/images/logo.png" 
              alt="RailBite Logo" 
              style={{ width: '135px', height: '45px', objectFit: 'contain' }}
            />
          </div>


          <ul className={`nav-menu ${mobileMenuOpen ? 'active' : ''}`} id="navMenu">
            <li>
              <Link 
                to="/" 
                className={isActive('/') ? 'active' : ''}
              >
                Home
              </Link>
            </li>
            <li>
              <Link 
                to="/about" 
                className={isActive('/about') ? 'active' : ''}
              >
                About Us
              </Link>
            </li>
            <li>
              <Link 
                to="/contact" 
                className={isActive('/contact') ? 'active' : ''}
              >
                Contact
              </Link>
            </li>
            {!isAuthenticated() && (
              <li id="authNavItem">
                <Link to="/login" className="btn-nav-orange">Login / Register</Link>
              </li>
            )}
          </ul>

          <div style={{ display: 'flex', alignItems: 'center', gap: '1.5rem' }}>
            {isAuthenticated() && (
              <>
                <div className="cart-icon" onClick={() => navigate('/cart')}>
                  ðŸ›’
                  {getCartCount() > 0 && (
                    <span className="cart-badge" id="cartCount">{getCartCount()}</span>
                  )}
                </div>
                <div 
                  className="user-icon" 
                  id="profileIcon"
                  onClick={() => navigate('/profile')}
                  style={{ fontSize: '1.8rem', cursor: 'pointer' }}
                >
                  ðŸ‘¤
                </div>
              </>
            )}

            <div 
              className={`hamburger ${mobileMenuOpen ? 'active' : ''}`} 
              id="hamburger"
              onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
            >
              <span></span>
              <span></span>
              <span></span>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
