import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useOrder } from '../context/OrderContext';
import BackButton from '../components/BackButton';
import Toast from '../components/Toast';

const OrderStation = () => {
  const [formData, setFormData] = useState({
    passengerName: '',
    phone: '',
    trainNumber: '',
    pickupStation: '',
    coachNumber: '',
    seatNumber: ''
  });

  const [toast, setToast] = useState(null);
  const { saveOrderType, saveBookingDetails } = useOrder();
  const navigate = useNavigate();

  const stations = [
    'Dhaka Kamalapur',
    'Chittagong',
    'Sylhet',
    'Rajshahi',
    'Comilla',
    'Mymensingh',
    'Khulna',
    'Rangpur'
  ];

  // Add options arrays for coach and seat
  const coachOptions = ['KA', 'KHA', 'GA', 'GHA', 'UMO', 'CHA', 'SHA', 'JA', 'JHA'];
  const seatOptions = Array.from({ length: 50 }, (_, i) => i + 1);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.passengerName || !formData.phone || !formData.trainNumber || !formData.pickupStation || !formData.coachNumber || !formData.seatNumber) {
      setToast({ message: 'Please fill in all fields', type: 'error' });
      return;
    }

    saveOrderType('station');
    saveBookingDetails({ ...formData, orderType: 'station' });
    
    localStorage.setItem('railbiteOrderType', 'Station');
    
    setToast({ message: 'Details saved! Redirecting to menu...', type: 'success' });
    
    setTimeout(() => {
      navigate('/menu-categories', { state: { orderType: 'Station' } });
    }, 1000);
  };

  return (
    <div className="booking-page">
      <BackButton />
      <div className="container">
        <div className="booking-container">
          <div className="booking-header">
            <div className="booking-header-icon"><img src="/images/station.png" alt="station" /></div>
            <div className="booking-header-text">
              <h2>Order from Station</h2>
              <p>Enter your journey details and pickup station</p>
            </div>
          </div>

          <form className="booking-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Passenger Name</label>
              <input
                type="text"
                name="passengerName"
                value={formData.passengerName}
                onChange={handleChange}
                placeholder="Enter passenger name"
                required
              />
            </div>

            <div className="form-group">
              <label>Phone Number</label>
              <input
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                placeholder="880 1XXX-XXXXXX"
                required
              />
            </div>

            <div className="form-group">
              <label>Train Number</label>
              <input
                type="text"
                name="trainNumber"
                value={formData.trainNumber}
                onChange={handleChange}
                placeholder="e.g., 701 Suborna Express"
                required
              />
            </div>

            <div className="form-group">
              <label>Pickup Station</label>
              <select
                name="pickupStation"
                value={formData.pickupStation}
                onChange={handleChange}
                required
              >
                <option value="">Select station</option>
                {stations.map((station) => (
                  <option key={station} value={station}>
                    {station}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Coach Number</label>
                <select
                  name="coachNumber"
                  value={formData.coachNumber}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select Coach</option>
                  {coachOptions.map((coach) => (
                    <option key={coach} value={coach}>
                      {coach}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label>Seat Number</label>
                <select
                  name="seatNumber"
                  value={formData.seatNumber}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select Seat</option>
                  {seatOptions.map((seat) => (
                    <option key={seat} value={seat}>
                      {seat}
                    </option>
                  ))}
                </select>
              </div>
            </div>

            <button type="submit" className="btn btn-primary btn-block">
              Continue to Menu
            </button>
          </form>
        </div>
      </div>

      {toast && (
        <Toast
          message={toast.message}
          type={toast.type}
          onClose={() => setToast(null)}
        />
      )}
    </div>
  );
};

export default OrderStation;
