import React from 'react';
import { useNavigate } from 'react-router-dom';
import BackButton from '../components/BackButton';

const MenuCategories = () => {
  const navigate = useNavigate();

  const categories = [
    { 
      name: 'Breakfast', 
      icon: 'üç≥', 
      description: 'Start your journey fresh',
      path: '/breakfast-menu',
      image: '/images/paratha.jpg'
    },
    { 
      name: 'Shwarma', 
      icon: 'üåØ', 
      description: 'Wraps & Rolls',
      path: '/shwarma-menu',
      image: '/images/shwarma.jpg'
    },
    { 
      name: 'Biryani', 
      icon: 'üçõ', 
      description: 'Authentic rice dishes',
      path: '/biryani-menu',
      image: '/images/biryani.jpg'
    },
    { 
      name: 'Pizza', 
      icon: 'üçï', 
      description: 'Fresh baked pizzas',
      path: '/pizza-menu',
      image: '/images/pizza.jpg'
    },
    { 
      name: 'Burgers', 
      icon: 'üçî', 
      description: 'Juicy burgers',
      path: '/burger-menu',
      image: '/images/burger.jpg'
    },
    { 
      name: 'Smoothies', 
      icon: 'ü•§', 
      description: 'Fresh & healthy',
      path: '/smoothie-menu',
      image: '/images/smoothie.jpg'
    }
  ];

  return (
    <div className="category-selection-page">
      <BackButton />
      <div className="container" style={{ paddingTop: '80px', paddingBottom: '80px' }}>
        <h1 className="section-title">Select Menu Category</h1>
        <p className="section-subtitle">
          Ordering from <span style={{ color: 'var(--primary-orange)', fontWeight: 600 }}>Train</span>
        </p>

        <div className="category-grid">
          {categories.map(category => (
            <div 
              key={category.name}
              className="category-card"
              onClick={() => navigate(category.path)}
              style={{
                backgroundImage: `url(${category.image})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
                position: 'relative',
                overflow: 'hidden',
                height: '250px'
              }}
            >
              <div style={{
                position: 'absolute',
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                background: 'linear-gradient(to bottom, rgba(3,4,9,0.6), rgba(3,4,9,0.9))',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'flex-end',
                padding: '2rem'
              }}>
                <div className="category-icon" style={{ marginBottom: '1rem' }}>
                  {category.icon}
                </div>
                <h3 style={{ marginBottom: '0.5rem' }}>{category.name}</h3>
                <p style={{ margin: 0, fontSize: '0.9rem' }}>{category.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default MenuCategories;
