import React, { useState, useMemo } from 'react';
import { useCart } from '../context/CartContext';
import BackButton from '../components/BackButton';
import SearchFilter from '../components/SearchFilter';

const BurgerMenu = () => {
  const { addToCart } = useCart();

  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('default');
  const [priceRange, setPriceRange] = useState('all');

  const menuItems = useMemo(() => [
    {
      id: 'classic-king-001',
      name: 'Classic King Burger',
      price: 180,
      image: 'images/classic-king.jpg',
      description: 'Juicy beef patty with cheese, lettuce, and special sauce'
    },
    {
      id: 'bbq-beef-002',
      name: 'BBQ Beef Blast',
      price: 220,
      image: 'images/bbq-beef-blast.jpg',
      description: 'Double beef patty with BBQ sauce and crispy onions'
    },
    {
      id: 'beef-pepperoni-003',
      name: 'Beef Pepperoni Burger',
      price: 250,
      image: 'images/beef-pepperoni.jpg',
      description: 'Beef burger topped with pepperoni and mozzarella'
    },
    {
      id: 'firecracker-chicken-004',
      name: 'Firecracker Chicken',
      price: 200,
      image: 'images/firecracker-chicken.jpg',
      description: 'Spicy chicken burger with jalapenos'
    }
  ], []);

  const filteredAndSortedItems = useMemo(() => {
    let filtered = [...menuItems];

    if (searchQuery) {
      filtered = filtered.filter(item =>
        item.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        item.description.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    if (priceRange !== 'all') {
      if (priceRange === '0-100') {
        filtered = filtered.filter(item => item.price < 100);
      } else if (priceRange === '100-200') {
        filtered = filtered.filter(item => item.price >= 100 && item.price < 200);
      } else if (priceRange === '200-300') {
        filtered = filtered.filter(item => item.price >= 200 && item.price < 300);
      } else if (priceRange === '300+') {
        filtered = filtered.filter(item => item.price >= 300);
      }
    }

    if (sortBy === 'name-asc') {
      filtered.sort((a, b) => a.name.localeCompare(b.name));
    } else if (sortBy === 'name-desc') {
      filtered.sort((a, b) => b.name.localeCompare(a.name));
    } else if (sortBy === 'price-low') {
      filtered.sort((a, b) => a.price - b.price);
    } else if (sortBy === 'price-high') {
      filtered.sort((a, b) => b.price - a.price);
    }

    return filtered;
  }, [menuItems, searchQuery, sortBy, priceRange]);

  const handleAddToCart = (item) => {
    addToCart(item);
    alert(`${item.name} added to cart!`);
  };

  return (
    <div className="menu-page">
      <BackButton />
      <div className="container">
        <div className="menu-header">
          <h1>üçî Burger Menu</h1>
          <p className="ordering-from">
            Delicious burgers for your cravings
          </p>
        </div>

        <SearchFilter
          searchQuery={searchQuery}
          onSearchChange={setSearchQuery}
          sortBy={sortBy}
          onSortChange={setSortBy}
          priceRange={priceRange}
          onPriceRangeChange={setPriceRange}
        />

        <div style={{ 
          marginBottom: '1.5rem', 
          color: 'var(--text-gray)',
          fontSize: '0.95rem'
        }}>
          {searchQuery && (
            <p>
              Found <strong style={{ color: 'var(--primary-orange)' }}>
                {filteredAndSortedItems.length}
              </strong> result(s) for "{searchQuery}"
            </p>
          )}
          {filteredAndSortedItems.length === 0 && searchQuery && (
            <p style={{ 
              color: 'var(--text-white)', 
              fontSize: '1.1rem', 
              textAlign: 'center', 
              padding: '2rem' 
            }}>
              No items found. Try a different search term.
            </p>
          )}
        </div>

        <div className="menu-grid">
          {filteredAndSortedItems.map((item) => (
            <div key={item.id} className="menu-item-card">
              <div className="menu-item-image">
                <img src={item.image} alt={item.name} />
              </div>
              <div className="menu-item-content">
                <h3 className="menu-item-title">{item.name}</h3>
                <p className="menu-item-description">{item.description}</p>
                <div className="menu-item-footer">
                  <p className="menu-item-price">‡ß≥{item.price}</p>
                  <button
                    className="btn btn-primary"
                    onClick={() => handleAddToCart(item)}
                  >
                    Add to Cart
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default BurgerMenu;
