import React, { createContext, useState, useContext, useEffect } from 'react';
import { authAPI } from '../services/api';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  // Load user from localStorage (includes role)
  const [user, setUser] = useState(() => {
    const stored = localStorage.getItem('railbiteUser');
    return stored ? JSON.parse(stored) : null;
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    checkAuth();
  }, []);

  // Check auth on initial load using stored token
  const checkAuth = async () => {
    const token = localStorage.getItem('railbiteToken');
    if (token) {
      try {
        const response = await authAPI.getMe();
        if (response.data.success && response.data.user) {
          // user should contain role from backend
          setUser(response.data.user);
        } else {
          localStorage.removeItem('railbiteToken');
          localStorage.removeItem('railbiteUser');
          setUser(null);
        }
      } catch (error) {
        console.error('Auth check failed:', error);
        localStorage.removeItem('railbiteToken');
        localStorage.removeItem('railbiteUser');
        setUser(null);
      }
    }
    setLoading(false);
  };

  // Unified login for normal users + admins
  const login = async (email, password) => {
    try {
      const response = await authAPI.login({ email, password }); // POST /auth/login
      if (response.data.success) {
        const { token, user } = response.data;
        // user must contain role from backend: 'user' or 'admin'
        localStorage.setItem('railbiteToken', token);
        localStorage.setItem('railbiteUser', JSON.stringify(user));
        setUser(user);
        return { success: true, user };
      }
      return { success: false, message: 'Login failed' };
    } catch (error) {
      const message = error.response?.data?.message || 'Login failed';
      return { success: false, message };
    }
  };

  const register = async (fullName, email, phone, password) => {
    try {
      const response = await authAPI.register({
        name: fullName,
        email,
        phone,
        password,
      });
      if (response.data.success) {
        const { token, user } = response.data;
        localStorage.setItem('railbiteToken', token);
        localStorage.setItem('railbiteUser', JSON.stringify(user));
        setUser(user);
        return { success: true, user };
      }
      return { success: false, message: 'Registration failed' };
    } catch (error) {
      const message = error.response?.data?.message || 'Registration failed';
      return { success: false, message };
    }
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('railbiteToken');
    localStorage.removeItem('railbiteUser');
    localStorage.removeItem('railbiteCart');
    localStorage.removeItem('railbiteOrderType');
    localStorage.removeItem('railbiteBooking');
  };

  const isAuthenticated = () => !!user;

  const value = {
    user,            // includes role: 'user' or 'admin'
    login,
    register,
    logout,
    isAuthenticated,
    loading,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
