import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import Toast from '../components/Toast';

const Login = () => {
  const navigate = useNavigate();
  const { login } = useAuth();

  const [mode, setMode] = useState('user'); // 'user' | 'delivery'
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [toast, setToast] = useState(null);
  const [loading, setLoading] = useState(false);

  const showToast = (message, type) => {
    setToast({ message, type });
    setTimeout(() => setToast(null), 2500);
  };

  const handleUserLogin = async (e) => {
    e.preventDefault();
    if (!email || !password) {
      showToast('Please fill in all fields', 'error');
      return;
    }

    setLoading(true);
    const result = await login(email, password);
    setLoading(false);

    if (!result.success) {
      showToast(result.message || 'Login failed', 'error');
      return;
    }

    const loggedUser = result.user;

    if (loggedUser.role === 'admin') {
      navigate('/admin/dashboard');
    } else {
      const intendedUrl = localStorage.getItem('railbiteIntendedUrl');
      if (intendedUrl) {
        localStorage.removeItem('railbiteIntendedUrl');
        navigate(intendedUrl);
      } else {
        navigate('/order-selection');
      }
    }
  };

  const handleDeliveryLogin = async (e) => {
    e.preventDefault();
    if (!email || !password) {
      showToast('Please fill in all fields', 'error');
      return;
    }

    try {
      setLoading(true);
      const res = await api.post('/auth/delivery/login', { email, password });
      setLoading(false);

      if (!res.data.success) {
        showToast(res.data.message || 'Delivery staff login failed', 'error');
        return;
      }

      const { token, staff } = res.data;
      localStorage.setItem('railbiteStaffToken', token);
      localStorage.setItem('railbiteStaff', JSON.stringify(staff));

      const redirect =
        staff.type === 'train'
          ? '/delivery/train/dashboard'
          : staff.type === 'station'
          ? '/delivery/station/dashboard'
          : '/delivery/dashboard';

      navigate(redirect);
    } catch (err) {
      setLoading(false);
      const message =
        err.response?.data?.message || 'Delivery staff login failed';
      showToast(message, 'error');
    }
  };

  const onSubmit = mode === 'user' ? handleUserLogin : handleDeliveryLogin;

  return (
    <div className="auth-page">
      <div className="auth-bg-overlay" />

      <div className="auth-layout">
        {/* Left side text / hero, same style as Register */}
        <div className="auth-hero">
          <h1 className="auth-hero-title">Bangladesh Railway Food Service</h1>
          <p className="auth-hero-subtitle">
            Order fresh meals to your seat or station while you travel with RailBite.
          </p>
          <ul className="auth-hero-list">
            <li>On-train and station pickup orders</li>
            <li>Real-time order tracking and notifications</li>
            <li>Secure payments and saved booking details</li>
          </ul>
        </div>

        {/* Right side card */}
        <div className="auth-card">
          <div className="auth-header centered">
            <h2>Sign in to RailBite</h2>
            <p>Choose how you want to login.</p>
          </div>

          {/* Toggle between user / delivery */}
          <div className="auth-toggle">
            <button
              type="button"
              className={`auth-toggle-btn ${mode === 'user' ? 'active' : ''}`}
              onClick={() => setMode('user')}
            >
              User Login
            </button>
            <button
              type="button"
              className={`auth-toggle-btn ${mode === 'delivery' ? 'active' : ''}`}
              onClick={() => setMode('delivery')}
            >
              Delivery Staff Login
            </button>
          </div>

          <form onSubmit={onSubmit} className="auth-form">
            <div className="form-group">
              <label className="form-label">Email</label>
              <input
                className="form-input"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                autoComplete="email"
              />
            </div>

            <div className="form-group">
              <label className="form-label">Password</label>
              <input
                className="form-input"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                autoComplete="current-password"
              />
            </div>

            <button
              type="submit"
              className="primary-btn full-width"
              disabled={loading}
            >
              {loading
                ? 'Logging in...'
                : mode === 'user'
                ? 'Login as User'
                : 'Login as Delivery Staff'}
            </button>
          </form>

          <div className="auth-footer">
            <span className="auth-footer-text">
              New to RailBite?{' '}
              <Link to="/register" className="auth-footer-link">
                Create account
              </Link>
            </span>
            <button
              type="button"
              className="link-btn"
              onClick={() => navigate('/forgot-password')}
            >
              Forgot password?
            </button>
            <button
              type="button"
              className="link-btn"
              onClick={() => navigate('/admin/login')}
            >
              Admin login
            </button>
          </div>
        </div>
      </div>

      {toast && (
        <Toast
          message={toast.message}
          type={toast.type}
          onClose={() => setToast(null)}
        />
      )}
    </div>
  );
};

export default Login;
