import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useOrder } from '../context/OrderContext';
import BackButton from '../components/BackButton';

function OrderTracking() {
  const { orderId } = useParams();
  const navigate = useNavigate();
  const { orders } = useOrder();
  const [order, setOrder] = useState(null);
  const [currentStatus, setCurrentStatus] = useState('confirmed');

  useEffect(() => {
    // Get order from context
    if (orders && orders.length > 0) {
      const foundOrder = orders.find((o) => o.id === orderId || o.orderId === orderId);
      if (foundOrder) {
        setOrder(foundOrder);
        setCurrentStatus(foundOrder.status || 'confirmed');
      }
    } else {
      // Mock order for demo
      const mockOrder = {
        orderId: orderId || 'ORD-20260106001',
        date: '2026-01-06',
        items: [
          { name: 'Bengali Biryani', quantity: 2, price: 250 },
          { name: 'Chicken Shwarma', quantity: 1, price: 120 }
        ],
        total: 620,
        status: 'confirmed',
        paymentMethod: 'bkash',
        customerInfo: {
          name: 'John Doe',
          phone: '01712345678',
          address: 'Coach A1, Seat 12'
        }
      };
      setOrder(mockOrder);
      setCurrentStatus('confirmed');
    }
  }, [orderId, orders]);

  const statusSteps = [
    { id: 'confirmed', label: 'Order Confirmed', icon: '‚úÖ', time: '2 min ago' },
    { id: 'preparing', label: 'Preparing Food', icon: 'üë®‚Äçüç≥', time: '5 min ago' },
    { id: 'ready', label: 'Ready for Delivery', icon: 'üì¶', time: 'In progress' },
    { id: 'ontheway', label: 'On the Way', icon: 'üöö', time: 'Pending' },
    { id: 'delivered', label: 'Delivered', icon: 'üéâ', time: 'Pending' }
  ];

  const getStatusIndex = (status) => {
    return statusSteps.findIndex((step) => step.id === status);
  };

  const currentIndex = getStatusIndex(currentStatus);

  if (!order) {
    return (
      <div className="order-tracking-page">
        <BackButton />
        <div className="container">
          <div className="empty-cart">
            <h2>No Order Found</h2>
            <p>Start ordering to track your food!</p>
            <button
              className="btn btn-primary"
              onClick={() => navigate('/menu-categories')}
            >
              Browse Menu
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="order-tracking-page">
      <BackButton />
      <div className="container">
        <div className="tracking-header">
          <h1>üöö Track Your Order</h1>
          <p className="order-id">
            Order ID: <strong>{order.orderId || order.id}</strong>
          </p>
        </div>

        <div className="tracking-container">
          <div className="order-status-timeline">
            {statusSteps.map((step, index) => (
              <div
                key={step.id}
                className={`timeline-step ${index <= currentIndex ? 'completed' : 'pending'}`}
              >
                <div className="step-icon">{step.icon}</div>
                <div className="step-content">
                  <h3>{step.label}</h3>
                  <p className="step-time">
                    {index <= currentIndex ? step.time : 'Pending'}
                  </p>
                </div>
                {index < statusSteps.length - 1 && (
                  <div className={`timeline-line ${index < currentIndex ? 'completed' : ''}`}></div>
                )}
              </div>
            ))}
          </div>

          <div className="order-details-card">
            <h3>Order Details</h3>
            <div className="detail-row">
              <span>Total Items</span>
              <strong>{order.items?.length || 0}</strong>
            </div>
            <div className="detail-row">
              <span>Total Amount</span>
              <strong className="price-text">‡ß≥{order.total}</strong>
            </div>
            <div className="detail-row">
              <span>Payment Method</span>
              <strong>{order.paymentMethod?.toUpperCase()}</strong>
            </div>
            <div className="detail-row">
              <span>Delivery Address</span>
              <strong>{order.customerInfo?.address || order.deliveryAddress || 'Train Seat'}</strong>
            </div>

            <div className="order-items-list">
              <h4>Items Ordered</h4>
              {order.items?.map((item, idx) => (
                <div key={idx} className="order-item-row">
                  <span>{item.name} x {item.quantity}</span>
                  <span>‡ß≥{item.price * item.quantity}</span>
                </div>
              ))}
            </div>

            <div className="tracking-actions">
              <button className="btn btn-secondary btn-block">
                üìû Contact Support
              </button>
              <button
                className="btn btn-primary btn-block"
                onClick={() => navigate('/')}
              >
                üè† Back to Home
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default OrderTracking;
