import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useOrder } from '../context/OrderContext';
import { useCart } from '../context/CartContext';
import BackButton from '../components/BackButton';
import Toast from '../components/Toast';

const OrderHistory = () => {
  const [filter, setFilter] = useState('all');
  const [toast, setToast] = useState(null);
  const { orderHistory } = useOrder();
  const { addToCart } = useCart();
  const navigate = useNavigate();

  const filteredOrders = filter === 'all' 
    ? orderHistory 
    : orderHistory.filter(order => order.status === filter);

  const handleReorder = (order) => {
    order.items.forEach(item => {
      for (let i = 0; i < item.quantity; i++) {
        addToCart(item.name, item.price);
      }
    });
    setToast({ message: 'Items added to cart! Ready to reorder.', type: 'success' });
    setTimeout(() => {
      navigate('/cart');
    }, 1500);
  };

  return (
    <div className="order-history-page">
      <BackButton />
      <div className="container">
        <div className="page-header-section">
          <h1 className="section-title">Order History</h1>
          <p className="section-subtitle">View all your past orders</p>
        </div>

        <div className="order-filters">
          <button 
            className={`filter-btn ${filter === 'all' ? 'active' : ''}`}
            onClick={() => setFilter('all')}
          >
            All Orders
          </button>
          <button 
            className={`filter-btn ${filter === 'delivered' ? 'active' : ''}`}
            onClick={() => setFilter('delivered')}
          >
            Delivered
          </button>
          <button 
            className={`filter-btn ${filter === 'pending' ? 'active' : ''}`}
            onClick={() => setFilter('pending')}
          >
            Pending
          </button>
          <button 
            className={`filter-btn ${filter === 'cancelled' ? 'active' : ''}`}
            onClick={() => setFilter('cancelled')}
          >
            Cancelled
          </button>
        </div>

        {filteredOrders.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">ðŸ“¦</div>
            <h2>No Orders Found</h2>
            <p>You haven't placed any orders yet. Start ordering delicious food now!</p>
            <button 
              className="btn btn-primary"
              onClick={() => navigate('/order-selection')}
            >
              Order Now
            </button>
          </div>
        ) : (
          <div className="order-history-list">
            {filteredOrders.map(order => (
              <div key={order.id} className="history-order-card">
                <div className="order-card-header">
                  <div className="order-id-date">
                    <span className="order-id-large">Order {order.id}</span>
                    <span className="order-date-time">
                      {new Date(order.date).toLocaleDateString('en-GB')} at{' '}
                      {new Date(order.date).toLocaleTimeString('en-US', {
                        hour: 'numeric',
                        minute: '2-digit',
                        hour12: true
                      })}
                    </span>
                  </div>
                  <span className={`order-status-badge ${order.status}`}>
                    {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                  </span>
                </div>

                <div className="order-card-body">
                  <div className="order-items-list">
                    {order.items.map((item, idx) => (
                      <div key={idx} className="order-item-row">
                        <span>
                          <span className="order-item-name">{item.name}</span>
                          <span className="order-item-qty"> x {item.quantity}</span>
                        </span>
                        <span className="order-item-price">
                          à§³{item.price * item.quantity}
                        </span>
                      </div>
                    ))}
                  </div>

                  {order.trainInfo && (
                    <div className="order-delivery-info">
                      <p><strong>Train:</strong> {order.trainInfo.trainNumber || 'N/A'}</p>
                      <p>
                        <strong>Coach:</strong> {order.trainInfo.coachNumber || 'N/A'}{' '}
                        <strong>Seat:</strong> {order.trainInfo.seatNumber || 'N/A'}
                      </p>
                      <p><strong>Payment:</strong> {
                        order.paymentMethod === 'mobile' ? 'Mobile Banking' :
                        order.paymentMethod === 'card' ? 'Card Payment' : 'Cash on Delivery'
                      }</p>
                    </div>
                  )}
                </div>

                <div className="order-card-footer">
                  <span className="order-total-amount">à§³{order.total}</span>
                  <div className="order-actions">
                    <button 
                      className="btn-view-receipt"
                      onClick={() => navigate(`/order-details/${order.id}`)}
                    >
                      View Receipt
                    </button>
                    {order.status === 'delivered' && (
                      <button 
                        className="btn-reorder"
                        onClick={() => handleReorder(order)}
                      >
                        Reorder
                      </button>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {toast && <Toast message={toast.message} type={toast.type} onClose={() => setToast(null)} />}
    </div>
  );
};

export default OrderHistory;
