import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useOrder } from '../context/OrderContext';
import BackButton from '../components/BackButton';

function OrderHistory() {
  const navigate = useNavigate();
  const {
    orderHistory,
    loadingOrders,
    orderError,
    fetchOrders,
  } = useOrder();

  const [orders, setOrders] = useState([]);
  const [filterStatus, setFilterStatus] = useState('all');

  // Load latest orders from backend
  useEffect(() => {
    fetchOrders();
  }, [fetchOrders]);

  // Sync context orders into local state
  useEffect(() => {
    setOrders(orderHistory || []);
  }, [orderHistory]);

  const getStatusBadge = (status) => {
    const badges = {
      delivered: { text: 'Delivered', class: 'delivered', icon: '‚úÖ' },
      cancelled: { text: 'Cancelled', class: 'cancelled', icon: '‚ùå' },
      processing: { text: 'Processing', class: 'processing', icon: 'üîÑ' },
    };
    return badges[status] || badges.processing;
  };

  const filteredOrders =
    filterStatus === 'all'
      ? orders
      : orders.filter((order) => order.status === filterStatus);

  const handleReorder = (order) => {
    alert('Items added to cart! Redirecting to menu...');
    navigate('/menu-categories');
  };

  const handleTrackOrder = (orderId) => {
    navigate(`/order-tracking/${orderId}`);
  };

  if (loadingOrders) {
    return (
      <div className="order-history-page">
        <BackButton />
        <div className="container">
          <div className="page-header-section">
            <h1>üì¶ Order History</h1>
            <p>Loading your orders...</p>
          </div>
        </div>
      </div>
    );
  }

  if (orderError) {
    return (
      <div className="order-history-page">
        <BackButton />
        <div className="container">
          <div className="page-header-section">
            <h1>üì¶ Order History</h1>
            <p style={{ color: 'red' }}>{orderError}</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="order-history-page">
      <BackButton />
      <div className="container">
        <div className="page-header-section">
          <h1>üì¶ Order History</h1>
          <p>View all your past orders</p>
        </div>

        <div className="order-filters">
          <button
            className={`filter-btn ${filterStatus === 'all' ? 'active' : ''}`}
            onClick={() => setFilterStatus('all')}
          >
            All Orders ({orders.length})
          </button>
          <button
            className={`filter-btn ${
              filterStatus === 'delivered' ? 'active' : ''
            }`}
            onClick={() => setFilterStatus('delivered')}
          >
            Delivered (
            {orders.filter((o) => o.status === 'delivered').length})
          </button>
          <button
            className={`filter-btn ${
              filterStatus === 'cancelled' ? 'active' : ''
            }`}
            onClick={() => setFilterStatus('cancelled')}
          >
            Cancelled (
            {orders.filter((o) => o.status === 'cancelled').length})
          </button>
        </div>

        {filteredOrders.length === 0 ? (
          <div className="empty-cart">
            <div className="empty-cart-icon">üì¶</div>
            <h2>No Orders Found</h2>
            <p>You haven't placed any orders yet.</p>
            <button
              className="btn btn-primary"
              onClick={() => navigate('/menu-categories')}
            >
              Start Ordering
            </button>
          </div>
        ) : (
          <div className="order-history-list">
            {filteredOrders.map((order) => {
              const badge = getStatusBadge(order.status);
              const orderId = order.orderId || order.id || order._id;
              const orderDate = order.date || order.orderDate;

              return (
                <div key={orderId} className="history-order-card">
                  <div className="order-card-header">
                    <div className="order-id-date">
                      <h3>Order #{orderId}</h3>
                      <p className="order-date-time">
                        {orderDate
                          ? new Date(orderDate).toLocaleDateString('en-US', {
                              year: 'numeric',
                              month: 'long',
                              day: 'numeric',
                            })
                          : 'Date not available'}
                      </p>
                    </div>
                    <span
                      className={`order-status-badge ${badge.class}`}
                    >
                      {badge.icon} {badge.text}
                    </span>
                  </div>

                  <div className="order-card-body">
                    <div className="order-items-list">
                      {order.items?.map((item, idx) => (
                        <div key={idx} className="order-item-row">
                          <span className="order-item-name">
                            {item.name} x {item.quantity}
                          </span>
                          <span className="order-item-price">
                            ‡ß≥{item.price * item.quantity}
                          </span>
                        </div>
                      ))}
                    </div>

                    {order.deliveryAddress && (
                      <div className="order-delivery-info">
                        <p>
                          Delivery to:{' '}
                          <strong>{order.deliveryAddress}</strong>
                        </p>
                      </div>
                    )}
                  </div>

                  <div className="order-card-footer">
                    <div className="order-total-amount">
                      ‡ß≥
                      {order.total?.toFixed
                        ? order.total.toFixed(2)
                        : order.total}
                    </div>
                    <div className="order-actions">
                      {order.status === 'delivered' && (
                        <button
                          className="btn btn-secondary btn-sm"
                          onClick={() => handleReorder(order)}
                        >
                          üîÑ Reorder
                        </button>
                      )}
                      <button
                        className="btn btn-primary btn-sm"
                        onClick={() => handleTrackOrder(orderId)}
                      >
                        üëÅÔ∏è View Details
                      </button>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
}

export default OrderHistory;
