import React, { createContext, useContext, useState, useEffect } from 'react';

const AdminContext = createContext();

export const useAdmin = () => useContext(AdminContext);

export const AdminProvider = ({ children }) => {
  const [adminUser, setAdminUser] = useState(null);
  const [isAdminAuthenticated, setIsAdminAuthenticated] = useState(false);

  useEffect(() => {
    const storedUser = localStorage.getItem('railbiteUser');
    if (storedUser) {
      const parsed = JSON.parse(storedUser);
      // ðŸ‘‡ Only allow admin role
      if (parsed.role === 'admin') {
        setAdminUser(parsed);
        setIsAdminAuthenticated(true);
      } else {
        // Clear any non-admin data
        localStorage.removeItem('railbiteToken');
        localStorage.removeItem('railbiteUser');
      }
    }
  }, []);

  const adminLoginSuccess = (user, token) => {
    // ðŸ‘‡ Only allow admin role
    if (user.role !== 'admin') {
      return { success: false, message: 'Access denied. Admin only.' };
    }
    setAdminUser(user);
    setIsAdminAuthenticated(true);
    return { success: true };
  };

  const adminLogout = () => {
    localStorage.removeItem('railbiteToken');
    localStorage.removeItem('railbiteuser');
    setAdminUser(null);
    setIsAdminAuthenticated(false);
  };

  return (
    <AdminContext.Provider
      value={{
        adminUser,
        isAdminAuthenticated,
        adminLoginSuccess,
        adminLogout
      }}
    >
      {children}
    </AdminContext.Provider>
  );
};
