import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import { useOrder } from '../context/OrderContext';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import BackButton from '../components/BackButton';
import Toast from '../components/Toast';

const Checkout = () => {
  const navigate = useNavigate();
  const { cart, clearCart } = useCart();
  const { user } = useAuth();
  const { addOrder } = useOrder();
  
  const [deliveryType, setDeliveryType] = useState('');
  const [trainInfo, setTrainInfo] = useState({
    trainName: '',
    trainNumber: '',
    coachNumber: '',
    seatNumber: '',
    boardingStation: '',
    destinationStation: '',
    journeyDate: ''
  });
  const [stationInfo, setStationInfo] = useState({
    stationName: '',
    pickupTime: ''
  });
  const [paymentMethod, setPaymentMethod] = useState('');
  
  // Mobile Banking State
  const [mobileBankingInfo, setMobileBankingInfo] = useState({
    provider: '',
    transactionId: ''
  });
  
  // Card Payment State
  const [cardInfo, setCardInfo] = useState({
    cardNumber: '',
    cardholderName: '',
    expiryDate: '',
    cvv: ''
  });
  
  const [toast, setToast] = useState({ show: false, message: '', type: '' });

  useEffect(() => {
    if (cart.length === 0) {
      navigate('/cart');
    }
  }, [cart, navigate]);

  const showToast = (message, type) => {
    setToast({ show: true, message, type });
  };

  const hideToast = () => {
    setToast({ show: false, message: '', type: '' });
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const handleTrainInfoChange = (e) => {
    setTrainInfo({
      ...trainInfo,
      [e.target.name]: e.target.value
    });
  };

  const handleStationInfoChange = (e) => {
    setStationInfo({
      ...stationInfo,
      [e.target.name]: e.target.value
    });
  };

  const handleMobileBankingChange = (e) => {
    setMobileBankingInfo({
      ...mobileBankingInfo,
      [e.target.name]: e.target.value
    });
  };

  const handleCardInfoChange = (e) => {
    let { name, value } = e.target;
    
    // Format card number with spaces
    if (name === 'cardNumber') {
      value = value.replace(/\s/g, '').replace(/(\d{4})/g, '$1 ').trim();
      if (value.length > 19) return; // Max 16 digits + 3 spaces
    }
    
    // Format expiry date
    if (name === 'expiryDate') {
      value = value.replace(/\D/g, '');
      if (value.length >= 2) {
        value = value.slice(0, 2) + '/' + value.slice(2, 4);
      }
      if (value.length > 5) return;
    }
    
    // Limit CVV to 3 digits
    if (name === 'cvv') {
      value = value.replace(/\D/g, '');
      if (value.length > 3) return;
    }
    
    setCardInfo({
      ...cardInfo,
      [name]: value
    });
  };

  const validateMobileBanking = () => {
    if (!mobileBankingInfo.provider) {
      showToast('Please select a mobile banking provider', 'error');
      return false;
    }
    if (!mobileBankingInfo.transactionId.trim()) {
      showToast('Please enter transaction ID', 'error');
      return false;
    }
    if (mobileBankingInfo.transactionId.length < 8) {
      showToast('Transaction ID must be at least 8 characters', 'error');
      return false;
    }
    return true;
  };

  const validateCardPayment = () => {
    const cardNumberDigits = cardInfo.cardNumber.replace(/\s/g, '');
    
    if (!cardInfo.cardNumber.trim()) {
      showToast('Please enter card number', 'error');
      return false;
    }
    if (cardNumberDigits.length !== 16) {
      showToast('Card number must be 16 digits', 'error');
      return false;
    }
    if (!cardInfo.cardholderName.trim()) {
      showToast('Please enter cardholder name', 'error');
      return false;
    }
    if (!cardInfo.expiryDate.trim() || cardInfo.expiryDate.length !== 5) {
      showToast('Please enter valid expiry date (MM/YY)', 'error');
      return false;
    }
    
    // Validate expiry date is not in the past
    const [month, year] = cardInfo.expiryDate.split('/');
    const expiry = new Date(2000 + parseInt(year), parseInt(month) - 1);
    const today = new Date();
    if (expiry < today) {
      showToast('Card has expired', 'error');
      return false;
    }
    
    if (!cardInfo.cvv.trim() || cardInfo.cvv.length !== 3) {
      showToast('Please enter valid 3-digit CVV', 'error');
      return false;
    }
    
    return true;
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate delivery type
    if (!deliveryType) {
      showToast('Please select delivery type', 'error');
      return;
    }

    // Validate delivery information
    if (deliveryType === 'train') {
      if (!trainInfo.trainName || !trainInfo.trainNumber || !trainInfo.coachNumber || 
          !trainInfo.seatNumber || !trainInfo.boardingStation || !trainInfo.destinationStation || 
          !trainInfo.journeyDate) {
        showToast('Please fill in all train information', 'error');
        return;
      }
    } else if (deliveryType === 'station') {
      if (!stationInfo.stationName || !stationInfo.pickupTime) {
        showToast('Please fill in all station information', 'error');
        return;
      }
    }

    // Validate payment method
    if (!paymentMethod) {
      showToast('Please select a payment method', 'error');
      return;
    }

    // Validate payment details based on method
    if (paymentMethod === 'mobileBanking') {
      if (!validateMobileBanking()) {
        return;
      }
    } else if (paymentMethod === 'card') {
      if (!validateCardPayment()) {
        return;
      }
    }

    // Create order object
    const order = {
      id: Date.now().toString(),
      userId: user.email,
      items: cart,
      total: calculateTotal(),
      deliveryType,
      deliveryInfo: deliveryType === 'train' ? trainInfo : stationInfo,
      paymentMethod,
      paymentInfo: paymentMethod === 'mobileBanking' 
        ? { provider: mobileBankingInfo.provider, transactionId: mobileBankingInfo.transactionId }
        : paymentMethod === 'card'
        ? { cardLastFour: cardInfo.cardNumber.slice(-4), cardholderName: cardInfo.cardholderName }
        : null,
      status: 'pending',
      orderDate: new Date().toISOString()
    };

    addOrder(order);
    clearCart();
    navigate('/order-success', { state: { orderId: order.id } });
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />
      <div className="container mx-auto px-4 py-8">
        <BackButton />
        <h1 className="text-3xl font-bold text-center mb-8">Checkout</h1>

        <div className="max-w-3xl mx-auto bg-white rounded-lg shadow-md p-6">
          <form onSubmit={handleSubmit}>
            {/* Delivery Type Selection */}
            

            {/* Payment Method Selection */}
            <div className="mb-6">
              <h2 className="text-xl font-semibold mb-4">Payment Method</h2>
              <div className="space-y-2">
                <label className="flex items-center space-x-2 cursor-pointer">
                  <input
                    type="radio"
                    name="paymentMethod"
                    value="cash"
                    checked={paymentMethod === 'cash'}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                    className="form-radio text-orange-500"
                  />
                  <span>Cash on Delivery</span>
                </label>
                <label className="flex items-center space-x-2 cursor-pointer">
                                    <input
                    type="radio"
                    name="paymentMethod"
                    value="mobileBanking"
                    checked={paymentMethod === 'mobileBanking'}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                    className="form-radio text-orange-500"
                  />
                  <span>Mobile Banking</span>
                </label>
                <label className="flex items-center space-x-2 cursor-pointer">
                  <input
                    type="radio"
                    name="paymentMethod"
                    value="card"
                    checked={paymentMethod === 'card'}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                    className="form-radio text-orange-500"
                  />
                  <span>Card Payment</span>
                </label>
              </div>
            </div>

            {/* Mobile Banking Details */}
            {paymentMethod === 'mobileBanking' && (
              <div className="mb-6 p-4 bg-gray-50 rounded-lg">
                <h3 className="text-lg font-semibold mb-3">Mobile Banking Details</h3>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Select Provider
                    </label>
                    <select
                      name="provider"
                      value={mobileBankingInfo.provider}
                      onChange={handleMobileBankingChange}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
                      required
                    >
                      <option value="">Choose Provider</option>
                      <option value="bkash">bKash</option>
                      <option value="nagad">Nagad</option>
                      <option value="rocket">Rocket</option>
                      <option value="upay">Upay</option>
                    </select>
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Transaction ID <span className="text-red-500">*</span>
                    </label>
                    <input
                      type="text"
                      name="transactionId"
                      placeholder="Enter your transaction ID"
                      value={mobileBankingInfo.transactionId}
                      onChange={handleMobileBankingChange}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
                      required
                    />
                    <p className="text-sm text-gray-500 mt-1">
                      Please complete the payment first and enter the transaction ID here
                    </p>
                  </div>
                </div>
              </div>
            )}

            {/* Card Payment Details */}
            {paymentMethod === 'card' && (
              <div className="mb-6 p-4 bg-gray-50 rounded-lg">
                <h3 className="text-lg font-semibold mb-3">Card Payment Details</h3>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Card Number <span className="text-red-500">*</span>
                    </label>
                    <input
                      type="text"
                      name="cardNumber"
                      placeholder="1234 5678 9012 3456"
                      value={cardInfo.cardNumber}
                      onChange={handleCardInfoChange}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Cardholder Name <span className="text-red-500">*</span>
                    </label>
                    <input
                      type="text"
                      name="cardholderName"
                      placeholder="John Doe"
                      value={cardInfo.cardholderName}
                      onChange={handleCardInfoChange}
                      className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
                      required
                    />
                  </div>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Expiry Date <span className="text-red-500">*</span>
                      </label>
                      <input
                        type="text"
                        name="expiryDate"
                        placeholder="MM/YY"
                        value={cardInfo.expiryDate}
                        onChange={handleCardInfoChange}
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
                        required
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        CVV <span className="text-red-500">*</span>
                      </label>
                      <input
                        type="password"
                        name="cvv"
                        placeholder="123"
                        value={cardInfo.cvv}
                        onChange={handleCardInfoChange}
                        maxLength="3"
                        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500"
                        required
                      />
                    </div>
                  </div>
                  <div className="flex items-start space-x-2 mt-2">
                    <svg className="w-5 h-5 text-gray-400 mt-0.5" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z" clipRule="evenodd" />
                    </svg>
                    <p className="text-sm text-gray-500">
                      Your payment information is secure and encrypted
                    </p>
                  </div>
                </div>
              </div>
            )}

            {/* Order Summary */}
            <div className="mb-6 p-4 bg-gray-50 rounded-lg">
              <h3 className="text-lg font-semibold mb-3">Order Summary</h3>
              <div className="space-y-2">
                {cart.map((item) => (
                  <div key={item.id} className="flex justify-between text-sm">
                    <span>{item.name} x {item.quantity}</span>
                    <span>৳{(item.price * item.quantity).toFixed(2)}</span>
                  </div>
                ))}
                <div className="border-t pt-2 mt-2">
                  <div className="flex justify-between font-bold text-lg">
                    <span>Total</span>
                    <span>৳{calculateTotal().toFixed(2)}</span>
                  </div>
                </div>
              </div>
            </div>

            {/* Submit Button */}
            <button
              type="submit"
              className="w-full bg-orange-500 text-white py-3 rounded-lg hover:bg-orange-600 transition duration-300 font-semibold"
            >
              Place Order
            </button>
          </form>
        </div>
      </div>
      <Footer />
      {toast.show && (
        <Toast 
          message={toast.message} 
          type={toast.type} 
          onClose={hideToast} 
        />
      )}
    </div>
  );
};

export default Checkout;
