import React, { useState, useEffect, useMemo } from 'react';
import AdminSidebar from '../components/AdminSidebar';
import { orderAPI } from '../services/api';

const AdminOrderManagement = () => {
  const [orders, setOrders] = useState([]);
  const [filter, setFilter] = useState('all');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        setLoading(true);
        setError(null);

        const token = localStorage.getItem('railbite_token');
        console.log('Token:', token);

        if (!token) {
          setError('Not authenticated');
          setLoading(false);
          return;
        }

        const res = await orderAPI.getAll(token);
        console.log('Orders response:', res.data);

        if (res.data.success) {
          setOrders(res.data.data || []);
        } else {
          setError(res.data.message || 'Failed to load orders');
        }
      } catch (err) {
        console.error('Orders fetch error:', err);
        setError(err.response?.data?.message || err.message || 'Error loading orders');
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  const handleMarkAsSent = async (order) => {
    if (!window.confirm(`Mark order #${order.orderNumber} as sent?`)) return;

    try {
      const token = localStorage.getItem('railbite_token');
      const res = await orderAPI.updateStatus(
        order._id,
        { status: 'confirmed', deliveryStatus: 'sent' },
        token
      );
      if (res.data.success) {
        setOrders((prev) =>
          prev.map((o) => (o._id === order._id ? res.data.data : o))
        );
        alert('Order marked as sent.');
      }
    } catch (err) {
      alert(err.response?.data?.message || err.message || 'Failed to update');
    }
  };

  const handleCancelOrder = async (order) => {
    if (!window.confirm('Are you sure you want to cancel this order?')) return;

    try {
      const token = localStorage.getItem('railbite_token');
      const res = await orderAPI.updateStatus(
        order._id,
        { status: 'cancelled' },
        token
      );
      if (res.data.success) {
        setOrders((prev) =>
          prev.map((o) => (o._id === order._id ? res.data.data : o))
        );
        alert('Order cancelled.');
      }
    } catch (err) {
      alert(err.response?.data?.message || err.message || 'Failed to cancel');
    }
  };

  const filteredOrders = useMemo(() => {
    return orders.filter((order) => {
      if (filter === 'all') return true;
      if (filter === 'pending') return order.status === 'pending';
      if (filter === 'preparing') return order.deliveryStatus === 'preparing';
      if (filter === 'sent') return order.deliveryStatus === 'sent';
      if (filter === 'delivered') return order.status === 'delivered';
      return true;
    });
  }, [orders, filter]);

  const getStatusBadge = (status) => {
    const badges = {
      pending: { text: 'Pending', class: 'status-pending' },
      confirmed: { text: 'Confirmed', class: 'status-preparing' },
      preparing: { text: 'Preparing', class: 'status-preparing' },
      on_the_way: { text: 'On the Way', class: 'status-sent' },
      sent: { text: 'Sent', class: 'status-sent' },
      delivered: { text: 'Delivered', class: 'status-delivered' },
      cancelled: { text: 'Cancelled', class: 'status-cancelled' }
    };
    return badges[status] || badges.pending;
  };

  if (loading) {
    return (
      <div className="admin-dashboard">
        <AdminSidebar />
        <div className="admin-content">
          <div className="admin-header">
            <h1>üì¶ Order Management</h1>
            <p>Loading orders...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="admin-dashboard">
        <AdminSidebar />
        <div className="admin-content">
          <div className="admin-header">
            <h1>üì¶ Order Management</h1>
            <p style={{ color: 'red' }}>Error: {error}</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      <AdminSidebar />
      <div className="admin-content">
        <div className="admin-header">
          <h1>üì¶ Order Management</h1>
          <p>Manage and track all customer orders</p>
        </div>

        {/* Filter Buttons */}
        <div className="admin-filters">
          <button
            className={`filter-btn ${filter === 'all' ? 'active' : ''}`}
            onClick={() => setFilter('all')}
          >
            All Orders ({orders.length})
          </button>
          <button
            className={`filter-btn ${filter === 'pending' ? 'active' : ''}`}
            onClick={() => setFilter('pending')}
          >
            Pending
          </button>
          <button
            className={`filter-btn ${filter === 'preparing' ? 'active' : ''}`}
            onClick={() => setFilter('preparing')}
          >
            Preparing
          </button>
          <button
            className={`filter-btn ${filter === 'sent' ? 'active' : ''}`}
            onClick={() => setFilter('sent')}
          >
            Sent
          </button>
          <button
            className={`filter-btn ${filter === 'delivered' ? 'active' : ''}`}
            onClick={() => setFilter('delivered')}
          >
            Delivered
          </button>
        </div>

        {/* Orders Table */}
        <div className="admin-table-container">
          <table className="admin-table">
            <thead>
              <tr>
                <th>Order ID</th>
                <th>Customer</th>
                <th>Items</th>
                <th>Total</th>
                <th>Date</th>
                <th>Status</th>
                <th>Delivery</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredOrders.length === 0 ? (
                <tr>
                  <td colSpan="8" style={{ textAlign: 'center', padding: '3rem' }}>
                    No orders found
                  </td>
                </tr>
              ) : (
                filteredOrders.map((order) => {
                  const badge = getStatusBadge(order.status);
                  return (
                    <tr key={order._id}>
                      <td>#{order.orderNumber || String(order._id).slice(-6)}</td>
                      <td>
                        <div>{order.user?.name || 'Customer'}</div>
                        <small style={{ color: 'var(--text-gray)' }}>
                          {order.user?.email || ''}
                        </small>
                      </td>
                      <td>
                        {order.items?.length} item{order.items?.length !== 1 ? 's' : ''}
                      </td>
                      <td style={{ fontWeight: '600', color: 'var(--primary-orange)' }}>
                        ‡ß≥{order.totalAmount?.toFixed(2)}
                      </td>
                      <td>
                        {new Date(order.createdAt).toLocaleDateString('en-BD', {
                          month: 'short',
                          day: 'numeric',
                          hour: '2-digit',
                          minute: '2-digit'
                        })}
                      </td>
                      <td>
                        <span className={`badge ${badge.class}`}>{badge.text}</span>
                      </td>
                      <td>
                        {order.deliveryStatus === 'sent' ? (
                          <span className="badge status-sent">üì¶ In Transit</span>
                        ) : order.status === 'delivered' ? (
                          <span className="badge status-delivered">‚úÖ Delivered</span>
                        ) : order.status === 'cancelled' ? (
                          <span className="badge status-cancelled">‚ùå Cancelled</span>
                        ) : (
                          <span className="badge status-preparing">üîÑ Preparing</span>
                        )}
                      </td>
                      <td>
                        <div className="action-buttons">
                          {order.deliveryStatus !== 'sent' &&
                            order.status !== 'delivered' &&
                            order.status !== 'cancelled' && (
                              <button
                                className="btn btn-primary btn-sm"
                                onClick={() => handleMarkAsSent(order)}
                              >
                                Mark as Sent
                              </button>
                            )}
                          {order.status !== 'cancelled' &&
                            order.status !== 'delivered' && (
                              <button
                                className="btn btn-danger btn-sm"
                                onClick={() => handleCancelOrder(order)}
                              >
                                Cancel
                              </button>
                            )}
                        </div>
                      </td>
                    </tr>
                  );
                })
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default AdminOrderManagement;
