import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useOrder } from '../context/OrderContext';
import { useCart } from '../context/CartContext';
import BackButton from '../components/BackButton';
import Toast from '../components/Toast';

const OrderDetails = () => {
  const { orderId } = useParams();
  const [order, setOrder] = useState(null);
  const [toast, setToast] = useState(null);
  const { getOrderById } = useOrder();
  const { addToCart } = useCart();
  const navigate = useNavigate();

  useEffect(() => {
    const foundOrder = getOrderById(orderId);
    if (foundOrder) {
      setOrder(foundOrder);
    } else {
      navigate('/order-history');
    }
  }, [orderId, getOrderById, navigate]);

  const handleReorder = () => {
    if (!order) return;
    
    order.items.forEach(item => {
      for (let i = 0; i < item.quantity; i++) {
        addToCart(item.name, item.price);
      }
    });
    setToast({ message: 'Items added to cart!', type: 'success' });
    setTimeout(() => {
      navigate('/cart');
    }, 1500);
  };

  if (!order) return null;

  return (
    <div className="order-details-page">
      <BackButton />
      <div className="container">
        <div className="receipt-container">
          <div className="receipt-header">
            <h1>Order Receipt</h1>
            <p className="receipt-id">{order.id}</p>
          </div>

          <div className="receipt-section">
            <h3>Order Information</h3>
            <div className="receipt-info-grid">
              <div className="receipt-info-item">
                <span className="label">Order Date</span>
                <span className="value">
                  {new Date(order.date).toLocaleDateString('en-GB')},{' '}
                  {new Date(order.date).toLocaleTimeString('en-US', {
                    hour: 'numeric',
                    minute: '2-digit',
                    hour12: true
                  })}
                </span>
              </div>
              <div className="receipt-info-item">
                <span className="label">Status</span>
                <span className="value">
                  <span className={`order-status-badge ${order.status}`}>
                    {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                  </span>
                </span>
              </div>
              <div className="receipt-info-item">
                <span className="label">Payment Method</span>
                <span className="value">
                  {order.paymentMethod === 'mobile' ? 'Mobile Banking' :
                   order.paymentMethod === 'card' ? 'Card Payment' : 'Cash on Delivery'}
                </span>
              </div>
            </div>
          </div>

          <div className="receipt-section">
            <h3>Customer Details</h3>
            <div className="receipt-info-grid">
              <div className="receipt-info-item">
                <span className="label">Name</span>
                <span className="value">{order.customer?.fullName || 'N/A'}</span>
              </div>
              <div className="receipt-info-item">
                <span className="label">Phone</span>
                <span className="value">{order.customer?.phone || 'N/A'}</span>
              </div>
            </div>
          </div>

          {order.trainInfo && (
            <div className="receipt-section">
              <h3>Delivery Details</h3>
              <div className="receipt-info-grid">
                <div className="receipt-info-item">
                  <span className="label">Train Number</span>
                  <span className="value">{order.trainInfo.trainNumber || 'N/A'}</span>
                </div>
                <div className="receipt-info-item">
                  <span className="label">Coach & Seat</span>
                  <span className="value">
                    {order.trainInfo.coachNumber || 'N/A'} - {order.trainInfo.seatNumber || 'N/A'}
                  </span>
                </div>
              </div>
            </div>
          )}

          <div className="receipt-section">
            <h3>Order Items</h3>
            <div className="receipt-items">
              {order.items.map((item, idx) => (
                <div key={idx} className="receipt-item-row">
                  <div className="receipt-item-info">
                    <span className="receipt-item-name">{item.name}</span>
                    <span className="receipt-item-qty"> x {item.quantity}</span>
                  </div>
                                    <span className="receipt-item-price">৳{item.price * item.quantity}</span>
                </div>
              ))}
            </div>
          </div>

          <div className="receipt-section">
            <h3>Payment Summary</h3>
            <div className="receipt-summary">
              <div className="receipt-summary-row">
                <span>Subtotal</span>
                <span>৳{order.subtotal}</span>
              </div>
              <div className="receipt-summary-row">
                <span>VAT (5%)</span>
                <span>৳{order.vat}</span>
              </div>
              <div className="receipt-summary-row">
                <span>Delivery Fee</span>
                <span>৳{order.deliveryFee}</span>
              </div>
              <div className="receipt-summary-row total">
                <span><strong>Total Amount</strong></span>
                <span><strong>৳{order.total}</strong></span>
              </div>
            </div>
          </div>

          <div className="receipt-actions">
            <button 
              className="btn btn-primary btn-block"
              onClick={handleReorder}
            >
              Reorder These Items
            </button>
            <button 
              className="btn btn-secondary btn-block"
              onClick={() => window.print()}
            >
              Print Receipt
            </button>
          </div>
        </div>
      </div>

      {toast && <Toast message={toast.message} type={toast.type} onClose={() => setToast(null)} />}
    </div>
  );
};

export default OrderDetails;
