import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import Toast from '../components/Toast';

const Login = () => {
  const navigate = useNavigate();
  const { login } = useAuth();

  const [mode, setMode] = useState('user'); // 'user' | 'delivery'
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [toast, setToast] = useState(null);
  const [loading, setLoading] = useState(false);

  const showToast = (message, type) => {
    setToast({ message, type });
    setTimeout(() => setToast(null), 2500);
  };

  const handleUserLogin = async (e) => {
    e.preventDefault();
    if (!email || !password) {
      showToast('Please fill in all fields', 'error');
      return;
    }

    setLoading(true);
    const result = await login(email, password);
    setLoading(false);

    if (!result.success) {
      showToast(result.message || 'Login failed', 'error');
      return;
    }

    const loggedUser = result.user;

    if (loggedUser.role === 'admin') {
      navigate('/admin/dashboard');
    } else {
      const intendedUrl = localStorage.getItem('railbiteIntendedUrl');
      if (intendedUrl) {
        localStorage.removeItem('railbiteIntendedUrl');
        navigate(intendedUrl);
      } else {
        navigate('/order-selection');
      }
    }
  };

  const handleDeliveryLogin = async (e) => {
    e.preventDefault();
    if (!email || !password) {
      showToast('Please fill in all fields', 'error');
      return;
    }

    try {
      setLoading(true);
      const res = await api.post('/auth/delivery/login', { email, password });
      setLoading(false);

      if (!res.data.success) {
        showToast(res.data.message || 'Delivery staff login failed', 'error');
        return;
      }

      const { token, staff } = res.data;
      localStorage.setItem('railbiteStaffToken', token);
      localStorage.setItem('railbiteStaff', JSON.stringify(staff));

      const redirect =
        staff.type === 'train'
          ? '/delivery/train/dashboard'
          : staff.type === 'station'
          ? '/delivery/station/dashboard'
          : '/delivery/dashboard';

      navigate(redirect);
    } catch (err) {
      setLoading(false);
      const message =
        err.response?.data?.message || 'Delivery staff login failed';
      showToast(message, 'error');
    }
  };

  const onSubmit = mode === 'user' ? handleUserLogin : handleDeliveryLogin;

  return (
    <div className="auth-page">
      <div className="auth-card">
        {/* LOGO AREA – same as Register */}
        <div className="auth-logo">
          {/* If you already use an <img> here in Register.js, copy that EXACT markup */}
          <img src="/images/railbite-logo.svg" alt="RailBite" className="auth-logo-image" />
          <p className="auth-logo-subtitle">Bangladesh Railway Food Service</p>
        </div>

        {/* HEADING – same position as Register */}
        <h2 className="auth-title-main">Login to Your Account</h2>

        {/* MODE TOGGLE – placed under title, but inside same card */}
        <div className="auth-toggle">
          <button
            type="button"
            className={`auth-toggle-btn ${mode === 'user' ? 'active' : ''}`}
            onClick={() => setMode('user')}
          >
            User Login
          </button>
          <button
            type="button"
            className={`auth-toggle-btn ${mode === 'delivery' ? 'active' : ''}`}
            onClick={() => setMode('delivery')}
          >
            Delivery Staff Login
          </button>
        </div>

        <form className="auth-form" onSubmit={onSubmit}>
          <div className="form-group">
            <label className="form-label">Email Address</label>
            <input
              className="form-input"
              type="email"
              placeholder="your.email@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              autoComplete="email"
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Password</label>
            <input
              className="form-input"
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              autoComplete="current-password"
              required
            />
          </div>

          <button
            type="submit"
            className="auth-submit-btn"
            disabled={loading}
          >
            {loading
              ? 'Logging in...'
              : mode === 'user'
              ? 'Login'
              : 'Login as Delivery Staff'}
          </button>
        </form>

        <div className="auth-footer-row">
          <button
            type="button"
            className="auth-footer-link"
            onClick={() => navigate('/forgot-password')}
          >
            Forgot password?
          </button>
        </div>

        <p className="auth-footer-text">
          Don&apos;t have an account?{' '}
          <Link to="/register" className="auth-footer-link">
            Register
          </Link>
        </p>

        <p className="auth-footer-text">
          Admin user?{' '}
          <button
            type="button"
            className="auth-footer-link"
            onClick={() => navigate('/admin/login')}
          >
            Admin Login
          </button>
        </p>
      </div>

      {toast && (
        <Toast
          message={toast.message}
          type={toast.type}
          onClose={() => setToast(null)}
        />
      )}
    </div>
  );
};

export default Login;
