import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useOrder } from '../context/OrderContext';
import BackButton from '../components/BackButton';
import Toast from '../components/Toast';

const OrderTrain = () => {
  const [formData, setFormData] = useState({
    passengerName: '',
    phone: '',
    trainNumber: '',
    coachNumber: '',
    seatNumber: ''
  });
  const [toast, setToast] = useState(null);
  const { saveOrderType, saveBookingDetails } = useOrder();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.passengerName || !formData.phone || !formData.trainNumber || 
        !formData.coachNumber || !formData.seatNumber) {
      setToast({ message: 'Please fill in all fields', type: 'error' });
      return;
    }

    saveOrderType('train');
    saveBookingDetails({
      ...formData,
      orderType: 'train'
    });

    setToast({ message: 'Details saved! Redirecting to menu...', type: 'success' });
    setTimeout(() => {
      navigate('/menu-categories');
    }, 1000);
  };

  return (
    <div className="booking-page">
      <BackButton />
      
      {/* Navigation */}
      <nav className="navbar">
        <div className="container">
          <div className="nav-wrapper">
            <div className="logo" onClick={() => navigate('/')}>
              <span>üçΩÔ∏è</span>
              <span>RailBite</span>
            </div>
          </div>
        </div>
      </nav>

      {/* Booking Form */}
      <div className="container">
        <div className="booking-container">
          <div className="booking-header">
            <div className="booking-header-icon">üöÇ</div>
            <div className="booking-header-text">
              <h2>Order from Train</h2>
              <p>Enter your journey details</p>
            </div>
          </div>

          <form className="booking-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Passenger Name</label>
              <input
                type="text"
                name="passengerName"
                value={formData.passengerName}
                onChange={handleChange}
                placeholder="Enter passenger name"
                required
              />
            </div>

            <div className="form-group">
              <label>Phone Number</label>
              <input
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                placeholder="880 1XXX-XXXXXX"
                required
              />
            </div>

            <div className="form-group">
              <label>Train Number</label>
              <input
                type="text"
                name="trainNumber"
                value={formData.trainNumber}
                onChange={handleChange}
                placeholder="e.g., 701 Suborna Express"
                required
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Coach Number</label>
                <input
                  type="text"
                  name="coachNumber"
                  value={formData.coachNumber}
                  onChange={handleChange}
                  placeholder="e.g., KA"
                  required
                />
              </div>

              <div className="form-group">
                <label>Seat Number</label>
                <input
                  type="text"
                  name="seatNumber"
                  value={formData.seatNumber}
                  onChange={handleChange}
                  placeholder="e.g., 25"
                  required
                />
              </div>
            </div>

            <button type="submit" className="btn btn-primary btn-block">
              Continue to Menu
            </button>
          </form>
        </div>
      </div>

      {toast && <Toast message={toast.message} type={toast.type} onClose={() => setToast(null)} />}
    </div>
  );
};

export default OrderTrain;
