import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

function NotificationBell() {
  const [notifications, setNotifications] = useState([]);
  const [isOpen, setIsOpen] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const panelRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Load notifications from localStorage
    const savedNotifications = localStorage.getItem('railbiteNotifications');
    if (savedNotifications) {
      const parsed = JSON.parse(savedNotifications);
      setNotifications(parsed);
      setUnreadCount(parsed.filter((n) => !n.read).length);
    } else {
      // Default notifications
      const defaultNotifications = [
        {
          id: 1,
          type: 'order',
          title: 'Order Confirmed',
          message: 'Your order #ORD-123 has been confirmed',
          time: new Date(Date.now() - 5 * 60 * 1000).toISOString(),
          read: false,
          icon: 'âœ…',
          link: '/order-history'
        },
        {
          id: 2,
          type: 'delivery',
          title: 'Out for Delivery',
          message: 'Your food is on the way!',
          time: new Date(Date.now() - 30 * 60 * 1000).toISOString(),
          read: false,
          icon: 'ðŸšš',
          link: '/order-tracking/ORD-123'
        },
        {
          id: 3,
          type: 'offer',
          title: 'Special Offer!',
          message: '20% off on all burgers today',
          time: new Date(Date.now() - 2 * 60 * 60 * 1000).toISOString(),
          read: false,
          icon: 'ðŸŽ‰',
          link: '/burger-menu'
        }
      ];
      setNotifications(defaultNotifications);
      setUnreadCount(3);
      localStorage.setItem('railbiteNotifications', JSON.stringify(defaultNotifications));
    }
  }, []);

  // Close panel when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (panelRef.current && !panelRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    };

    if (isOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    }
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isOpen]);

  const saveNotifications = (updatedNotifications) => {
    setNotifications(updatedNotifications);
    localStorage.setItem('railbiteNotifications', JSON.stringify(updatedNotifications));
    setUnreadCount(updatedNotifications.filter((n) => !n.read).length);
  };

  const handleNotificationClick = (notification) => {
    // Mark as read
    const updated = notifications.map((n) =>
      n.id === notification.id ? { ...n, read: true } : n
    );
    saveNotifications(updated);
    setIsOpen(false);
    
    // Navigate to link
    if (notification.link) {
      navigate(notification.link);
    }
  };

  const markAllAsRead = () => {
    const updated = notifications.map((n) => ({ ...n, read: true }));
    saveNotifications(updated);
  };

  const clearAll = () => {
    setNotifications([]);
    setUnreadCount(0);
    localStorage.removeItem('railbiteNotifications');
  };

  const getRelativeTime = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInSeconds = Math.floor((now - date) / 1000);

    if (diffInSeconds < 60) return 'Just now';
    if (diffInSeconds < 3600) return `${Math.floor(diffInSeconds / 60)}m ago`;
    if (diffInSeconds < 86400) return `${Math.floor(diffInSeconds / 3600)}h ago`;
    if (diffInSeconds < 604800) return `${Math.floor(diffInSeconds / 86400)}d ago`;
    return `${Math.floor(diffInSeconds / 604800)}w ago`;
  };

  return (
    <div className="notification-bell-container" ref={panelRef}>
      <div 
        className="notification-bell" 
        onClick={() => setIsOpen(!isOpen)}
        style={{ position: 'relative', cursor: 'pointer' }}
      >
        <span style={{ fontSize: '1.8rem' }}>ðŸ””</span>
        {unreadCount > 0 && (
          <span className="notification-badge">
            {unreadCount > 9 ? '9+' : unreadCount}
          </span>
        )}
      </div>

      {isOpen && (
        <div className="notification-panel">
          <div className="notification-header">
            <h3>Notifications</h3>
            {notifications.length > 0 && (
              <div style={{ display: 'flex', gap: '0.5rem' }}>
                <button 
                  className="mark-all-btn" 
                  onClick={markAllAsRead}
                  style={{ fontSize: '0.75rem' }}
                >
                  Mark all read
                </button>
                <button 
                  className="mark-all-btn" 
                  onClick={clearAll}
                  style={{ fontSize: '0.75rem', color: 'var(--danger)' }}
                >
                  Clear all
                </button>
              </div>
            )}
          </div>

          <div className="notifications-list">
            {notifications.length === 0 ? (
              <div className="no-notifications">
                <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>ðŸ”•</div>
                <p>No notifications</p>
                <small>You're all caught up!</small>
              </div>
            ) : (
              notifications.map((notification) => (
                <div
                  key={notification.id}
                  className={`notification-item ${notification.read ? '' : 'unread'}`}
                  onClick={() => handleNotificationClick(notification)}
                >
                  <div className="notification-icon">{notification.icon}</div>
                  <div className="notification-content">
                    <p style={{ fontWeight: notification.read ? 'normal' : 'bold' }}>
                      {notification.title}
                    </p>
                    <small className="notification-message">
                      {notification.message}
                    </small>
                    <span className="notification-time">
                      {getRelativeTime(notification.time)}
                    </span>
                  </div>
                  {!notification.read && <div className="unread-dot"></div>}
                </div>
              ))
            )}
          </div>

          {notifications.length > 0 && (
            <div style={{ 
              padding: '1rem', 
              textAlign: 'center', 
              borderTop: '1px solid var(--border-dark)' 
            }}>
              <button
                className="btn btn-secondary btn-sm"
                onClick={() => {
                  setIsOpen(false);
                  navigate('/notifications');
                }}
                style={{ width: '100%' }}
              >
                View All Notifications
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default NotificationBell;
