import React, { useState } from 'react';
import { useCart } from '../context/CartContext';
import BackButton from '../components/BackButton';
import Toast from '../components/Toast';

const SmoothieMenu = () => {
  const [toast, setToast] = useState(null);
  const { addToCart } = useCart();

  const smoothieItems = [
    { 
      name: 'Mango Smoothie', 
      price: 199, 
      image: '/images/mango-smoothie.jpg',
      description: 'Fresh mango blended with yogurt'
    },
    { 
      name: 'Strawberry Smoothie', 
      price: 219, 
      image: '/images/strawberry-smoothie.jpg',
      description: 'Sweet strawberries with cream'
    },
    { 
      name: 'Mixed Fruits Smoothie', 
      price: 249, 
      image: '/images/mix-smoothie.jpg',
      description: 'Blend of tropical fruits'
    }
  ];

  const handleAddToCart = (name, price) => {
    addToCart(name, price);
    setToast({ message: `${name} added to cart!`, type: 'success' });
  };

  return (
    <div className="menu-page">
      <BackButton />
      <div className="container">
        <h1 style={{ fontSize: '2.5rem', marginBottom: '3rem', textAlign: 'center' }}>
          Smoothie Selection
        </h1>

        <div className="menu-grid-bordered">
          {smoothieItems.map(item => (
            <div key={item.name} className="menu-card-bordered">
              <div className="menu-card-image">
                <img 
                                    src={item.image} 
                  alt={item.name}
                  style={{
                    width: '200px',
                    height: '200px',
                    objectFit: 'cover',
                    borderRadius: '15px'
                  }}
                />
              </div>
              <div className="menu-card-info">
                <h3>{item.name.toUpperCase()}</h3>
                <p style={{ 
                  color: 'var(--text-gray)', 
                  fontSize: '0.9rem', 
                  marginBottom: '1rem' 
                }}>
                  {item.description}
                </p>
                <p className="price">à§³{item.price}</p>
                <button 
                  className="btn btn-primary"
                  onClick={() => handleAddToCart(item.name, item.price)}
                >
                  Order Now
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {toast && <Toast message={toast.message} type={toast.type} onClose={() => setToast(null)} />}
    </div>
  );
};

export default SmoothieMenu;
