import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useCart } from '../context/CartContext';
import NotificationBell from './NotificationBell';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { isAuthenticated, logout } = useAuth();
  const { cart } = useCart();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const cartItemCount = cart.reduce((total, item) => total + item.quantity, 0);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <nav className="navbar">
      <div className="container">
        <div className="nav-wrapper">
          {/* Logo */}
          <div className="logo" onClick={() => navigate('/')}>
            <img src="/images/logo.png" alt="RailBite Logo" style={{ width: '135px', height: '45px' }} />
          </div>

          {/* Nav Menu */}
          <ul className={`nav-menu ${isMenuOpen ? 'active' : ''}`} id="navMenu">
            <li>
              <a href="/" className={location.pathname === '/' ? 'active' : ''}>
                Home
              </a>
            </li>
            <li>
              <a href="/about" className={location.pathname === '/about' ? 'active' : ''}>
                About Us
              </a>
            </li>
            <li>
              <a href="/contact" className={location.pathname === '/contact' ? 'active' : ''}>
                Contact
              </a>
            </li>
            {isAuthenticated && (
              <li>
                <button onClick={handleLogout} className="btn-logout">
                  Logout
                </button>
              </li>
            )}
          </ul>

          {/* Right Side Icons */}
          <div style={{ display: 'flex', alignItems: 'center', gap: '1.5rem' }}>
            {/* Notification Bell */}
            {isAuthenticated && <NotificationBell />}

            {/* Cart Icon */}
            <div className="cart-icon" onClick={() => navigate('/cart')}>
              ðŸ›’
              {cartItemCount > 0 && (
                <span className="cart-badge" id="cartCount">
                  {cartItemCount}
                </span>
              )}
            </div>

            {/* User Icon */}
            <div
              className="user-icon"
              onClick={() => navigate(isAuthenticated ? '/profile' : '/login')}
              style={{ fontSize: '1.8rem', cursor: 'pointer' }}
            >
              ðŸ‘¤
            </div>

            {/* Hamburger */}
            <div
              className="hamburger"
              id="hamburger"
              onClick={() => setIsMenuOpen(!isMenuOpen)}
            >
              <span></span>
              <span></span>
              <span></span>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
