import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useOrder } from '../context/OrderContext';
import { useAuth } from '../context/AuthContext';
import BackButton from '../components/BackButton';
import Toast from '../components/Toast';

const Checkout = () => {
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [toast, setToast] = useState(null);
  const { cart, getSubtotal, getTotal, clearCart } = useCart();
  const { addOrder, bookingDetails } = useOrder();
  const { user } = useAuth();
  const navigate = useNavigate();

  const subtotal = getSubtotal();
  const vat = Math.round(subtotal * 0.05);
  const deliveryFee = 50;
  const total = getTotal();

  const handlePlaceOrder = () => {
    if (cart.length === 0) {
      setToast({ message: 'Your cart is empty!', type: 'error' });
      return;
    }

    const order = {
      items: cart.map(item => ({
        name: item.name,
        quantity: item.quantity,
        price: item.price
      })),
      customer: {
        fullName: user?.name || 'Guest User',
        email: user?.email || 'guest@example.com',
        phone: user?.phone || bookingDetails?.phone || '01XXXXXXXXX'
      },
      paymentMethod,
      subtotal,
      vat,
      deliveryFee,
      total,
      trainInfo: bookingDetails
    };

    addOrder(order);
    clearCart();
    setToast({ message: 'Order placed successfully!', type: 'success' });
    
    setTimeout(() => {
      navigate('/order-success');
    }, 1000);
  };

  return (
    <div className="checkout-page">
      <BackButton />
      <div className="container">
        <h1 style={{ fontSize: '2.5rem', marginBottom: '2rem' }}>Checkout</h1>

        <div className="checkout-container">
          <div>
            <div className="checkout-section">
              <h3>Order Summary</h3>
              {cart.map(item => (
                <div key={item.id} className="summary-row">
                  <span>{item.name} x {item.quantity}</span>
                  <span>à§³{item.price * item.quantity}</span>
                </div>
              ))}
            </div>

            <div className="checkout-section">
              <h3>Payment Method</h3>
              <div className="payment-methods">
                <div 
                  className={`payment-method ${paymentMethod === 'cash' ? 'active' : ''}`}
                  onClick={() => setPaymentMethod('cash')}
                >
                  <div className="payment-method-icon">ðŸ’µ</div>
                  <h4>Cash</h4>
                  <p>Pay on delivery</p>
                </div>

                <div 
                  className={`payment-method ${paymentMethod === 'mobile' ? 'active' : ''}`}
                  onClick={() => setPaymentMethod('mobile')}
                >
                  <div className="payment-method-icon">ðŸ“±</div>
                  <h4>Mobile Banking</h4>
                  <p>bKash / Nagad / Rocket</p>
                </div>

                <div 
                  className={`payment-method ${paymentMethod === 'card' ? 'active' : ''}`}
                  onClick={() => setPaymentMethod('card')}
                >
                  <div className="payment-method-icon">ðŸ’³</div>
                  <h4>Card</h4>
                  <p>Credit / Debit Card</p>
                </div>
              </div>

              {paymentMethod === 'cash' && (
                <div className="payment-details">
                  <h4>Cash on Delivery</h4>
                  <p>Please keep exact change ready. Our delivery partner will collect <strong>à§³{total}</strong> when your food arrives at your seat.</p>
                </div>
              )}

              {paymentMethod === 'mobile' && (
                <div className="payment-details">
                  <h4>Mobile Banking Payment</h4>
                  <p>1. Open your mobile banking app (bKash/Nagad/Rocket)</p>
                  <p>2. Send <strong>à§³{total}</strong> to our merchant number</p>
                  <p>3. Save the transaction ID for confirmation</p>
                </div>
              )}

              {paymentMethod === 'card' && (
                <div className="payment-details">
                  <h4>Card Payment</h4>
                  <p>You will be redirected to a secure payment gateway to complete your transaction of <strong>à§³{total}</strong></p>
                </div>
              )}
            </div>
          </div>

          <div className="cart-summary">
            <h3>Payment Summary</h3>
            <div className="summary-row">
              <span>Subtotal</span>
              <span>à§³{subtotal}</span>
            </div>
            <div className="summary-row">
              <span>VAT (5%)</span>
              <span>à§³{vat}</span>
            </div>
            <div className="summary-row">
              <span>Delivery Fee</span>
              <span>à§³{deliveryFee}</span>
            </div>
            <div className="summary-row">
              <span><strong>Total</strong></span>
              <span><strong>à§³{total}</strong></span>
            </div>
            <button 
              className="btn btn-primary btn-block"
              onClick={handlePlaceOrder}
            >
              Place Order
            </button>
          </div>
        </div>
      </div>

      {toast && <Toast message={toast.message} type={toast.type} onClose={() => setToast(null)} />}
    </div>
  );
};

export default Checkout;
