import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useMenu } from '../context/MenuContext';
import BackButton from '../components/BackButton';

function MenuCategories() {
  const navigate = useNavigate();
  const { categories, loading, error } = useMenu();

  const categoryConfig = {
    breakfast: { icon: 'üç≥', path: '/breakfast-menu', color: '#FF6B6B' },
    lunch: { icon: 'üçõ', path: '/lunch-menu', color: '#4ECDC4' },
    dinner: { icon: 'üçΩÔ∏è', path: '/dinner-menu', color: '#95E1D3' },
    snacks: { icon: 'üçø', path: '/snacks-menu', color: '#F38181' },
    burger: { icon: 'üçî', path: '/burger-menu', color: '#FFA726' },
    pizza: { icon: 'üçï', path: '/pizza-menu', color: '#EF5350' },
    biryani: { icon: 'üçö', path: '/biryani-menu', color: '#FFA000' },
    shwarma: { icon: 'üåØ', path: '/shwarma-menu', color: '#8BC34A' },
    smoothie: { icon: 'ü•§', path: '/smoothie-menu', color: '#E91E63' }
  };

  const handleCategoryClick = (category) => {
    const config = categoryConfig[category.toLowerCase()];
    if (config) {
      navigate(config.path);
    }
  };

  if (loading) {
    return (
      <div className="menu-categories-page">
        <BackButton />
        <div className="container">
          <h1>Menu Categories</h1>
          <div className="loading-state">
            <div className="spinner"></div>
            <p>Loading menu...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="menu-categories-page">
        <BackButton />
        <div className="container">
          <h1>Menu Categories</h1>
          <div className="error-state">
            <p>{error}</p>
            <button className="btn btn-primary" onClick={() => window.location.reload()}>
              Retry
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="menu-categories-page">
      <BackButton />
      <div className="container">
        <h1>Menu Categories</h1>
        <p className="subtitle">Choose your favorite category</p>

        <div className="categories-grid">
          {categories.map((category) => {
            const config = categoryConfig[category.toLowerCase()] || {
              icon: 'üç¥',
              path: `/${category.toLowerCase()}-menu`,
              color: '#9C27B0'
            };

            return (
              <div
                key={category}
                className="category-card"
                onClick={() => handleCategoryClick(category)}
                style={{ borderColor: config.color }}
              >
                <div className="category-icon" style={{ backgroundColor: config.color }}>
                  {config.icon}
                </div>
                <h3>{category.charAt(0).toUpperCase() + category.slice(1)}</h3>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default MenuCategories;
