import React from 'react';
import { useNavigate } from 'react-router-dom';
import BackButton from '../components/BackButton';

const MenuCategories = () => {
  const navigate = useNavigate();

  const categories = [
    { name: 'Shwarma', icon: 'üåØ', path: '/shwarma-menu' },
    { name: 'Biryani', icon: 'üçõ', path: '/biryani-menu' },
    { name: 'Pizza', icon: 'üçï', path: '/pizza-menu' },
    { name: 'Burgers', icon: 'üçî', path: '/burger-menu' },
    { name: 'Smoothies', icon: 'ü•§', path: '/smoothie-menu' },
    { name: 'Breakfast', icon: 'üç≥', path: '/breakfast-menu' }
  ];

  return (
    <div className="category-selection-page">
      <BackButton />
      <div className="container" style={{ paddingTop: '80px', paddingBottom: '80px' }}>
        <h1 style={{ fontSize: '2.5rem', textAlign: 'center', marginBottom: '3rem' }}>
          Select a Category
        </h1>

        <div className="category-grid">
          {categories.map(category => (
            <div 
              key={category.name}
              className="category-card"
              onClick={() => navigate(category.path)}
            >
              <div className="category-icon">{category.icon}</div>
              <h3>{category.name}</h3>
              <p>Explore our delicious {category.name.toLowerCase()} menu</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default MenuCategories;
